std::string markdownToHTML(std::string &markdown) {
    std::string html = "";

    // Loop through the characters in the markdown string
    for (int i = 0; i < markdown.length(); i++) {
        // Check for bold
        if (markdown[i] == '*') {
            // Determine if it is the beginning or end of a bold tag
            if (markdown[i+1] == '*') {
                html += "</strong>";
            } else {
                html += "<strong>";
            }
            // Skip the next character
            i++;
        }
        // Check for italic
        else if (markdown[i] == '_') {
            // Determine if it is the beginning or end of an italic tag
            if (markdown[i+1] == '_') {
                html += "</em>";
            } else {
                html += "<em>";
            }
            // Skip the next character
            i++;
        }
        // Check for headers
        else if (markdown[i] == '#') {
            // Count the number of '#' characters
            int headerCount = 0;
            while (markdown[i] == '#') {
                headerCount++;
                i++;
            }
            // Add the appropriate header tag
            html += "<h" + std::to_string(headerCount) + ">";
        }
        // Check for list items
        else if (markdown[i] == '-') {
            html += "<li>";
        }
        // Otherwise, add the character to the html string
        else {
            html += markdown[i];
        }
    }

    return html;
}

function isPrimeNumber(arr) {
  for(let i = 0; i < arr.length; i++) {
    let num = arr[i];
    let isPrime = true;

    // Check if num is divisible by any number from 2 to num - 1
    for(let j = 2; j < num; j++) {
      if(num % j === 0) {
        isPrime = false;
        break;
      }
    }
    // If num is prime, print it to the console
    if(isPrime) {
      console.log(num);
    }
  }
